document.addEventListener("DOMContentLoaded", function () {
    // Lấy giá trị bookIndex từ URL
    const urlParams = new URLSearchParams(window.location.search);
    const bookIndex = urlParams.get("bookIndex");
    window.addEventListener("scroll", function () {
        let searchBar = document.querySelector(".containner-main-search");
        let scrollPosition = document.documentElement.scrollTop;

        if (scrollPosition > 140) {
            searchBar.classList.add("fixed-search-bar");
        } else {
            searchBar.classList.remove("fixed-search-bar");
        }
    });
    handleonclick(bookIndex);
});

function handleonclick(bookIndex) {
    const nameBookAkaElement = document.getElementById("name-book-aka");
    const bookImage = document.querySelector(".img-inject");
    const midInformationBook = document.getElementById("mid-information-book");
    const productPrices = document.querySelector(".product-prices");
    const button = document.querySelector(".button-prices-containner");
    const informationthisbook = document.querySelector(
        ".information-this-book"
    );
    const renderinfor = document.querySelector(".render-information-full");
    const cartLink = document.getElementById("cart-link");

    if (bookIndex !== null && bookIndex >= 0 && bookIndex < book.length) {
        const currentBook = book[bookIndex];

        nameBookAkaElement.textContent = currentBook.name;
        bookImage.src = currentBook.image;
        midInformationBook.innerHTML = information(currentBook);
        productPrices.innerHTML = generatePrices(currentBook);
        button.innerHTML = buttonHandle(currentBook);
        informationthisbook.innerHTML = present(currentBook);
        renderinfor.innerHTML = renderinformation(currentBook);
    } else {
        nameBookAkaElement.textContent = "null đeo dau";
        bookImage.src = "";
        midInformationBook.innerHTML = "";
    }
    function buttonHandle(book) {
        if (book.button === "Nhận Thông Báo Cho Sản Phẩm") {
            return `<button type="button" class="button-prices">${book.button}</button>`;
        } else if (book.button === "Mua Ngay") {
            return `<a class="yellowbutton-button" href="#" onclick="addToCart('${book.name}', '${book.price}', '${book.imagesmall}')">${book.button}
      </a>`;
        }
    }

    window.addToCart = function (name, price, imageSmall) {
        console.log("addToCart is called");
        const selectedBooks =
            JSON.parse(localStorage.getItem("selectedBooks")) || [];

        const selectedBook = {
            name: name,
            price: price,
            image: imageSmall,
        };

        selectedBooks.push(selectedBook);

        localStorage.setItem("selectedBooks", JSON.stringify(selectedBooks));
        window.location.reload();
    };
    function renderinformation(book) {
        return `
    <div class="containner-thongtinchitiet">
    <h3 class="title-book">Thông Tin Chi Tiết</h3>
    <div class="containner-full-information-book">
    <div class="box-containner-full-infor">
    <div class="flex-text">
    <span class="width-145">Tác Giả:</span><span>${book.author}</span>
    </div>
    <div class="flex-text">
    <span class="width-145">Nhà Phát Hành:</span><span>${book.NpH}</span></div>
    <div class="flex-text">
    <span class="width-145">Khối Lượng:</span><span>${book.KL}</span></div>
    <div class="flex-text">
    <span class="width-145">Định Dạng:</span><span>${book.dingdang}</span></div>
    <div class="flex-text">
    <span class="width-145">Ngày Phát Hành:</span><span>${book.releasedate}</span></div>
    </div>
    <div class="box-containner-full-infor">
    <div class="flex-text">
    <span class="width-145">Nhà Suất Bản:</span><span>${book.NSB}</span></div>
    <div class="flex-text">
    <span class="width-145">Mã Sản Phẩm:</span><span>${book.masp}</span></div>
    <div class="flex-text">
    <span class="width-145">Ngôn Ngữ:</span><span>${book.language}</span></div>
    <div class="flex-text">
    <span class="width-145">Kíck Thước:</span><span>${book.KT}</span></div>
    <div class="flex-text">
    <span class="width-145">Số Trang:</span><span>${book.sotrang}</span></div>
    </div>
    </div>
    </div>
    `;
    }
    function present(book) {
        return `
    <div class="main-containner-javascript">
   <h3 class="title-book">${book.name}</h3>
   <div class="product-info-right">
   <div class="information-price">Thông Tin Tác Giả</div>
   <div class="author-a">${book.author}</div>
   <a href="#" class="read-more">Vào Trang Riêng Của Tác Giả</a>
   <a href="#" class="read-more">Xem Tất Cả Sáck Của Tác Giả</a>
   </div>
   <p class="name-information-book-gt">${book.name}</p>
   <div class="text-information-full">${book.informationfull}</div>
   </div>
   `;
    }

    function information(book) {
        return `
      <h3 class="mainbox-title">${book.name}</h3>
      <div>Tác giả: <span class="gray-gray-text">${book.author}</span></div>
      <div>Nhà suất Bản: ${book.NSB}</div>
      <div>Nhà Phát Hành: <span class="gray-gray-text">${book.NpH}</span></div>
      <div><span class="line-height-gray">${book.information}<a href="#">Xem Thêm</a></div>
    `;
    }
    function generatePrices(book) {
        return `
     <div class="flex-product-prices">
     <span>Giá Bìa</span>
     <span class="text-underline-through">${book.infomationprice}</span>
     </div>
     <div class="flex-product-prices">
     <span>Giá Bán</span>
     <span class="text-sale">${book.price}</span>
     </div>
     <div class="flex-product-prices">
     <span>Tiết Kiệm</span>
     <span class="text-save">${book.save}</span>
     </div>
     <div class="flex-product-prices">
     <span>Chất Lượng Sáck</span>
     <span class="text-quality">${book.quality}</span>
     </div>
     <div class=" fiend">
     <span class="text-have">${book.have}</span>
     </div>
     <div class=" fiend-free">
     <span class="text-have-free">${book.free}</span>
     </div>
     `;
    }
}
